import { useQuery } from '@tanstack/react-query';
import axios from 'axios';

const useConfig = () => {
  const root = '//' + window.location.host + '/api/v1';

  const fetch = async () => {
    const response = await axios.get<string[]>(root + '/config/domains').then((response) => {
      return response.data;
    });

    return { managedDomains: response };
  };

  return { fetch: useQuery(['config'], () => fetch()) };
};

export default useConfig;
